
import React from 'react';

const MostVisitedPlaces = () => {
  const places = [
    {
      name: <a href="https://en.wikipedia.org/wiki/Eiffel_Tower">Eiffel Tower</a>,
      location: 'Paris, France',
      description: 'An iconic symbol of France and one of the most recognizable structures in the world.',
      imageUrl: 'data:image/jpeg;base64,'
    },
    {
      name: <a href="https://en.wikipedia.org/wiki/Great_Wall_of_China">'Great Wall Of China'</a>,
      location: 'China',
      description: 'An ancient series of walls and fortifications, totaling more than 13,000 miles in length.',
      imageUrl: 'https://img.freepik.com/premium-photo/great-wall-china_839169-5375.jpg'
    },
    {
      name: <a href="https://www.nps.gov/stli/index.htm">'Statue Of Liberty'</a>,
      location: 'New York City, USA',
      description: 'A symbol of freedom and democracy, gifted by France to the United States in 1886.',
      imageUrl: 'data:image/jpeg;base64,'
    },
    {
      name: <a href="https://en.wikipedia.org/wiki/Machu_Picchu">'Machu Picchu'</a>,
      location: 'Peru',
      description: 'An ancient Incan city set high in the Andes Mountains, renowned for its archaeological significance.',
      imageUrl: 'https://img.freepik.com/free-photo/high-angle-beautiful-machu-picchu-citadel-surrounded-by-foggy-mountains-urubamba-peru_181624-1395.jpg'
    },
    {
      name: <a href="https://www.britannica.com/topic/Colosseum">Colosseum</a>,
      location: 'Rome, Italy',
      description: 'An ancient amphitheater that once hosted gladiator contests and public spectacles.',
      imageUrl: 'data:image/jpeg;base64,'
    }
  ];

  const pageStyle = {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    padding: '20px',
    fontFamily: 'Arial, sans-serif'
  };

  const headerStyle = {
    fontSize: '36px',
    fontWeight: 'bold',
    marginBottom: '20px'
  };

  const placeStyle = {
    marginBottom: '20px',
    padding: '20px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    width: '100%',
    maxWidth: '600px',
    boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    textAlign: 'center'
  };

  const placeNameStyle = {
    fontSize: '24px',
    fontWeight: 'bold'
  };

  const placeLocationStyle = {
    fontSize: '18px',
    color: 'gray'
  };

  const placeDescriptionStyle = {
    fontSize: '16px',
    marginTop: '10px'
  };

  const placeImageStyle = {
    width: '100%',
    height: 'auto',
    borderRadius: '5px',
    marginBottom: '10px'
  };

  return (
    <div style={pageStyle}>
      {/* <h1 style={headerStyle}>Most Visited Places in the World</h1> */}
      {places.map((place, index) => (
        <div key={index} style={placeStyle}>
          <img src={place.imageUrl} alt={place.name} style={placeImageStyle} />
          <div style={placeNameStyle}>{place.name}</div>
          <div style={placeLocationStyle}>{place.location}</div>
          <div style={placeDescriptionStyle}>{place.description}</div>
        </div>
      ))}
    </div>
  );
};

export default MostVisitedPlaces;

